import {IOrder, OrderSide, OrderStatus} from "../orderInterface";

export interface IAnalyzer {
    analyze(order: IOrder): void;
}

/**
 * The CapitalAnalyzer calculates wins ans losses made by using the orders
 * your strategy placed
 */
export default class CapitalAnalyzer implements IAnalyzer {

    /**
     * Creates a new CapitalAnalyzer
     * @param {number} startCapital your starting capital
     */
    constructor(private startCapital: number) {
        // TODO do something with starting capital
    }

    /**
     * Calculates your new capital based on a fulfilled order and compares
     * it to your old capital
     * @param {IOrder} order fulfilled order
     */
    analyze(order: IOrder) {
        if (order.status !== OrderStatus.FILLED) {
            return;
        }

        const oldCapital = this.startCapital;
        if (order.side === OrderSide.BUY) {
            this.startCapital -= (order.price * order.quantity);
        } else if (order.side === OrderSide.SELL) {
            this.startCapital += (order.price * order.quantity);
        }

        console.log("capital: ", this.startCapital, " : ", this.calcPerc(oldCapital, this.startCapital), "\n");
    }

    /**
     * Calculates percentage gain or loss
     * @param {number} a old capital
     * @param {number} b new capital
     * @returns {string} percentage gain or loss
     */
    calcPerc(a: number, b: number): string {
        if (b > a) {
            return `+${(b - a) / a * 100}%`;
        } else {
            return `-${(b - a) / b * 100}%`;
        }
    }
}

import fs from "fs";
import mkdirp from "mkdirp";
import {lowercase, numbers, uppercase} from "nanoid-dictionary";
import generate from "nanoid/generate";
import path from "path";
import rimraf from "rimraf";
import util from "util";
import {ICandle} from "./candleCollection";
import CandleLoader, {Parser} from "./candleLoader";
import localExchange from "./exchanges/localExchange";
import LocalExchange from "./exchanges/localExchange";
import {IOrder} from "./orderInterface";
import SockTrader, {ISockTraderConfig} from "./sockTrader";

/**
 * The BackTester enables you to test your strategy against a fake dummy exchange
 * and optimize to the point of content
 */
export default class BackTester extends SockTrader {

    private static CACHE_FOLDER = path.resolve(".sockTrader");
    private candleLoader?: CandleLoader;

    /**
     * Creates a new BackTester
     * @param {ISockTraderConfig} config
     */
    constructor(config: ISockTraderConfig = {webServer: true}) {
        super(config);

        this.exchange = localExchange.getInstance();
    }

    /**
     * Sets the loader responsible for loading local file data into
     * an in memory candle collection
     * @param {string} inputPath the path to the file containing candles
     * @param {Parser} parser the parser for transforming the data
     * @returns {this}
     */
    setCandleLoader(inputPath: string, parser?: Parser): this {
        this.candleLoader = new CandleLoader(inputPath, parser);

        return this;
    }

    async start(): Promise<void> {
        await super.start();

        if (!this.candleLoader) {
            throw new Error("No candle loader defined.");
        }

        if (!this.eventsBound) {
            this.subscribeToExchangeEvents(this.strategyConfigurations);

            this.strategyConfigurations.forEach(c => {
                const strategy = new c.strategy(c.pair, this.exchange);
                this.bindStrategyToExchange(strategy);
                this.bindExchangeToStrategy(strategy);
                this.bindExchangeToSocketServer();
            });

            this.eventsBound = true;
        }

        const candles: ICandle[] = (await this.candleLoader.parse()).toArray();
        if (this.webServer) {
            await BackTester.createCache();
            await BackTester.clearCache();
            await this.writeCandleCache(candles);
        }

        await (this.exchange as LocalExchange).emitCandles(candles);
    }

    private static async clearCache(): Promise<void> {
        const rmrf = util.promisify(rimraf);
        await rmrf(`${BackTester.CACHE_FOLDER}/*`);
    }

    private static async createCache(): Promise<void> {
        const mkdir = util.promisify(mkdirp);
        await mkdir(BackTester.CACHE_FOLDER);
    }

    private bindExchangeToSocketServer() {
        this.exchange.on("app.report", (order: IOrder) => this.sendToWebServer("REPORT", order));
    }

    private writeCandleCache(candles: ICandle[]): Promise<string> {
        return new Promise((resolve, reject) => {
            const fileName = generate(`${lowercase}${uppercase}${numbers}`, 6);
            const cacheFile = path.resolve(BackTester.CACHE_FOLDER, `${fileName}.json`);

            const response = JSON.stringify({type: "CANDLES", payload: {pair: "", candles}});
            fs.writeFile(cacheFile, response, err => {
                if (err) return reject(err);

                this.sendToWebServer("CANDLE_FILE", cacheFile);
                resolve(cacheFile);
            });
        });

    }
}

import {CronJob} from "cron";
import parser from "cron-parser";
import {EventEmitter} from "events";
import moment, {Moment} from "moment";
import config from "../config";
import logger from "./logger";

export interface ICandle {
    close: number;
    high: number;
    low: number;
    open: number;
    timestamp: Moment;
    volume: number;
}

export interface ICandleInterval {
    code: string;
    cron: string;
}

export interface IIntervalDict {
    [key: string]: ICandleInterval;
}

/**
 * Contains OHLCV history data for a trading pair.
 * The collection will automatically fill holes when initial and new data is pushed
 * The collection can automatically generate values if no new values were pushed during a time interval
 */
export default class CandleCollection extends EventEmitter {
    private candles: ICandle[] = [];
    private cronjob: CronJob;

    /**
     * Creates a new CandleCollection
     * @param {ICandleInterval} interval the interval between the candles
     * @param {boolean} generateCandles if should generate candles when nothing is received
     * @param {number} retentionPeriod how long to keep candles
     */
    constructor(private interval: ICandleInterval, generateCandles = true, private retentionPeriod = 0) {
        super();

        const candleGenerator = this.generateCandles.bind(this);
        this.cronjob = new CronJob(interval.cron, candleGenerator, undefined, generateCandles, config.timezone);
    }

    /**
     * Immediately set/replace all candles in the collection
     */
    set(candles: ICandle[]): void {
        const interval = parser.parseExpression(this.interval.cron, {
            endDate: candles[0].timestamp.toDate(),
            currentDate: new Date(),
        });

        this.candles = this.fillCandleGaps(candles, interval).reverse();
        this.emit("update", this.candles);
    }

    sort = (candles: ICandle[]): ICandle[] => candles.sort((a, b) => b.timestamp.diff(a.timestamp));

    /**
     * Stop automatic candle generator
     */
    stop() {
        this.cronjob.stop();
    }

    /**
     * Update or insert newly received candles
     * @param {ICandle[]} candles candles to add
     */
    update(candles: ICandle[]): void {
        let needsSort = false;
        candles.forEach(updatedCandle => {
            const candleUpdated = this.candles.some((candle, idx) => {
                if (this.candleEqualsTimestamp(candle, updatedCandle.timestamp)) {
                    this.candles[idx] = updatedCandle;
                    return true;
                }
                return false;
            });

            if (!candleUpdated) {
                const l = this.candles.unshift(updatedCandle);
                this.removeRetentionOverflow(this.candles);

                // You need at least 2 candles to sort the list..
                if (l >= 2 && !this.candles[0].timestamp.isAfter(this.candles[1].timestamp, "minute")) {
                    logger.error(`Server has changed candle history! Suspected candle: ${JSON.stringify(updatedCandle)}`);
                    needsSort = true;
                }
            }
        });

        if (needsSort) {
            this.candles = this.sort(this.candles);
        }

        this.emit("update", this.candles);
    }

    /**
     * Validates if a candle occurs on a certain timestamp
     * @param {ICandle} candle the candle
     * @param {moment.Moment} timestamp the time
     * @returns {boolean} occurs on given time
     */
    private candleEqualsTimestamp(candle: ICandle, timestamp: Moment): boolean {
        return candle.timestamp.isSameOrAfter(timestamp, "minute");
    }

    /**
     * Fill gaps in candle list until now, based on a cron expression
     * @param {ICandle[]} candles candle collection to fill
     * @param interval interval for which to fill
     * @returns {ICandle[]} the filled collection
     */
    private fillCandleGaps(candles: ICandle[], interval: any): ICandle[] {
        candles = this.sort(candles);

        const result: ICandle[] = [];
        const generateCandle = this.getCandleGenerator(candles);

        while (true) {
            const nextInterval: Moment = moment(interval.prev().toDate());
            const candle = generateCandle(nextInterval);

            result.unshift(candle);

            if (result.length === this.retentionPeriod) break;

            if (this.candleEqualsTimestamp(candles[candles.length - 1], nextInterval)) break;
        }

        return result;
    }

    /**
     * Generate new candles on each candle interval
     */
    private generateCandles(): void {
        const last = moment(this.cronjob.lastDate()).second(0).millisecond(0);

        if (this.candles.length <= 0) {
            this.candles.unshift(this.getRecycledCandle({close: 0} as ICandle, last));
        } else if (last.isAfter(this.candles[0].timestamp, "minute")) {
            this.candles.unshift(this.getRecycledCandle(this.candles[0], last));
        }

        this.removeRetentionOverflow(this.candles);
        this.emit("update", this.candles);
    }

    /**
     * Returns a function which will either return a new candle or recycle a previous candle
     * This function should be executed on every timer tick so that even though no values
     * changed, the candle collection receives a 'new' candle
     * @param {ICandle[]} candle collection
     * @returns {(interval: moment.Moment) => ICandle} candle generator
     */
    private getCandleGenerator(candles: ICandle[]): (interval: Moment) => ICandle {
        let position = 0;
        return nextInterval => {
            let candle = candles[position];

            if (!this.candleEqualsTimestamp(candle, nextInterval)) {
                candle = this.getRecycledCandle(candles[position], nextInterval);
            } else {
                position += 1;
            }

            return candle;
        };
    }

    /**
     * Copy candle based on the close value of a different candle
     * for indicating an interval without price change
     * @param {number} close the close
     * @param {moment.Moment} timestamp the timestamp
     * @returns {ICandle} copied candle
     */
    private getRecycledCandle = ({close}: ICandle, timestamp: Moment): ICandle => ({
        open: close,
        high: close,
        low: close,
        close,
        volume: 0,
        timestamp,
    })

    /**
     * Removes candles outside the retention period
     * @param {ICandle[]} candles the candle collection
     */
    private removeRetentionOverflow(candles: ICandle[]): void {
        if (this.retentionPeriod > 0 && candles.length > this.retentionPeriod) {
            candles.splice(this.retentionPeriod - candles.length);
        }
    }
}

import {IDataFrame} from "data-forge";
import {IAsyncFileReader, readFile} from "data-forge-fs";

export type Parser = (candles: IDataFrame<number, any>) => IDataFrame<number, any>;

/**
 * The CandleLoader parses a file containing candles and returns
 * in data for in memory processing
 */
export default class CandleLoader {

    constructor(private filePath: string, private parser?: Parser) {
    }

    /**
     * Parses file depending on the extension/type
     * @param {IAsyncFileReader} fileReader the file to read
     * @param {string} extension the extension
     * @returns {Promise<IDataFrame<number, any>>} promise
     */
    static async parseFileReader(fileReader: IAsyncFileReader, extension: string): Promise<IDataFrame<number, any>> {
        if (extension === "json") {
            return await fileReader.parseJSON();
        }

        if (extension === "csv") {
            return await fileReader.parseCSV({dynamicTyping: true});
        }

        throw new Error("File extension is not valid! Expecting a CSV or JSON file.");
    }

    /**
     * Actual parsing of file returning data
     * @returns {Promise<IDataFrame<number, any>>}
     */
    async parse(): Promise<IDataFrame<number, any>> {
        const segs = this.filePath.split(".");
        const ext = segs[segs.length - 1].toLowerCase();

        const dataFrame = await CandleLoader.parseFileReader(readFile(this.filePath), ext);
        return (this.parser) ? this.parser(dataFrame) : dataFrame;
    }
}

import {Decimal} from "decimal.js-light";
import {EventEmitter} from "events";
import {lowercase, numbers, uppercase} from "nanoid-dictionary";
import generate from "nanoid/generate";
import {Error} from "tslint/lib/error";
import {client as WebSocketClient, connection, IMessage} from "websocket";
import CandleCollection, {ICandle, ICandleInterval} from "../candleCollection";
import logger from "../logger";
import Orderbook, {IOrderbookEntry} from "../orderbook";
import {IOrder, OrderSide, OrderStatus, ReportType} from "../orderInterface";
import {Pair} from "../types/pair";
import {IExchange} from "./exchangeInterface";

export interface IResponseMapper {
    onReceive(msg: IMessage): void;
}

export interface ICurrencyMap {
    [key: string]: ITradeablePair;
}

export interface ITradeablePair {
    id: Pair;
    quantityIncrement: number;
    tickSize: number;
}

export interface IOrderbookData {
    ask: IOrderbookEntry[];
    bid: IOrderbookEntry[];
    pair: Pair;
    sequence: number;
}

/**
 * The BaseExchange resembles common marketplace functionality
 */
export default abstract class BaseExchange extends EventEmitter implements IExchange {
    currencies: ICurrencyMap = {};
    isAuthenticated = false;
    isCurrenciesLoaded = false;
    protected candles: { [key: string]: CandleCollection } = {};
    protected openOrders: IOrder[] = [];
    protected socketClient: WebSocketClient = new WebSocketClient();
    private connection?: connection;
    private orderbooks: { [key: string]: Orderbook } = {};
    private orderIncrement = 0;
    private orderInProgress: { [key: string]: boolean } = {};
    private ready = false;

    protected constructor() {
        super();

        this.onCreate();
    }

    abstract adjustOrder(order: IOrder, price: number, qty: number): void;

    buy(pair: Pair, price: number, qty: number): void {
        this.createOrder(pair, price, qty, OrderSide.BUY);
    }

    abstract cancelOrder(order: IOrder): void;

    connect(connectionString: string): void {
        this.socketClient.on("connectFailed", error => logger.error("Connect Error: " + error.toString()));
        this.socketClient.on("connect", (conn: connection) => this.onConnect(conn));

        this.socketClient.connect(connectionString);
    }

    abstract createOrder(pair: Pair, price: number, qty: number, side: OrderSide): void;

    destroy(): void {
        this.removeAllListeners();
    }

    /**
     * Wraps the emit and notifies if no listeners where found
     * @param {string | symbol} event to throw
     * @param args event arguments
     * @returns {boolean} if listeners where found
     */
    emit(event: string | symbol, ...args: any[]): boolean {
        const result = super.emit(event, ...args);
        // TODO create config for all debug statements only to be active in dev
        if (!result && process.env.NODE_ENV === "dev") {
            logger.debug(`No listener found for: "${event.toString()}"`);
        }

        return result;
    }

    /**
     * Generates orderId based on trading pair, timestamp, increment and random string. With max length 32 characters
     * ex: 15COVETH1531299734778DkXBry9y-sQ
     * @param pair crypto pair (BTC USD/BTC ETH)
     * @returns {string} order id
     */
    generateOrderId(pair: Pair): string {
        this.orderIncrement += 1;

        const alphabet = `${lowercase}${uppercase}${numbers}_-.|`;
        const orderId = `${this.orderIncrement}${pair}${new Date().getTime()}`;

        return orderId + generate(alphabet, 32 - orderId.length);
    }

    /**
     * Returns candle collection for pair and interval
     * @param {Pair} pair crypto pair (BTC USD/BTC ETH)
     * @param {ICandleInterval} interval time interval
     * @param {(candles: CandleCollection) => void} updateHandler what to do if candle collection updates
     * @returns {CandleCollection} the candle collection
     */
    getCandleCollection(pair: Pair, interval: ICandleInterval, updateHandler: (candles: CandleCollection) => void): CandleCollection {
        const key = `${pair}_${interval.code}`;
        if (this.candles[key]) {
            return this.candles[key];
        }

        this.candles[key] = new CandleCollection(interval);
        this.candles[key].on("update", updateHandler);
        return this.candles[key];
    }

    getOpenOrders = (): IOrder[] => this.openOrders;

    getOrderbook(pair: Pair): Orderbook {
        const ticker = pair.join("");
        if (this.orderbooks[ticker]) {
            return this.orderbooks[ticker];
        }

        const config = this.currencies[ticker];
        if (!config) {
            throw new Error(`No configuration found for pair: "${ticker}"`);
        }

        const precision = new Decimal(config.tickSize).decimalPlaces();

        this.orderbooks[ticker] = new Orderbook(pair, precision);
        return this.orderbooks[ticker];
    }

    /**
     * Verify if the exchange is ready and trigger the "ready" event if ready.
     * Can be called multiple times.. it will trigger the "ready" event only once.
     * @returns {boolean}
     */
    isReady(): boolean {
        if (this.ready) {
            return this.ready;
        }

        if (this.isCurrenciesLoaded && this.isAuthenticated) {
            this.ready = true;
            this.emit("ready");
        }

        return this.ready;
    }

    /**
     * Wrapper for the on method to log registration of listeners
     * @param {string} event the event to register to
     * @param {(...args: any[]) => void} listener the listeners to bind
     * @returns {this} exchange
     */
    on(event: string, listener: (...args: any[]) => void): this {
        // TODO create config for all debug statements only to be active in dev
        if (process.env.NODE_ENV === "dev") {
            logger.debug(`Listener created for: "${event.toString()}"`);
        }
        return super.on(event, listener);
    }

    onCreate(): void {
        setInterval(() => {
            this.orderIncrement = 0;
        }, 1000 * 60 * 5); // Reset increment every 5 minutes
    }

    onCurrenciesLoaded(currencies: ITradeablePair[]): void {
        currencies.forEach(currency => this.currencies[currency.id.join("")] = currency);
        this.isCurrenciesLoaded = true;
        this.isReady();
    }

    onReport(order: IOrder): void {
        const orderId = order.id;
        let oldOrder: IOrder | undefined;

        this.setOrderInProgress(orderId, false);

        if (order.reportType === ReportType.REPLACED && order.originalId) {
            const oldOrderId = order.originalId;

            oldOrder = this.openOrders.find(oo => oo.id === oldOrderId);
            this.setOrderInProgress(oldOrderId, false);
            this.removeOrder(oldOrderId);
            this.addOrder(order); // Order is replaced with a new one
        } else if (order.reportType === ReportType.NEW) {
            this.addOrder(order); // New order created
        } else if (order.reportType === ReportType.TRADE && order.status === OrderStatus.FILLED) {
            this.removeOrder(orderId); // Order is 100% filled
        } else if ([ReportType.CANCELED, ReportType.EXPIRED, ReportType.SUSPENDED].indexOf(order.reportType) > -1) {
            this.removeOrder(orderId); // Order is invalid
        }

        this.emit("app.report", order, oldOrder);
    }

    abstract onUpdateCandles<K extends keyof CandleCollection>(pair: Pair, data: ICandle[], interval: ICandleInterval, method: Extract<K, "set" | "update">): void;

    abstract onUpdateOrderbook<K extends keyof Orderbook>(data: IOrderbookData, method: Extract<K, "setOrders" | "addIncrement">): void;

    sell(pair: Pair, price: number, qty: number): void {
        this.createOrder(pair, price, qty, OrderSide.SELL);
    }

    /**
     * Send request over socket connection
     * @param {string} method the type of send
     * @param {object} params the data
     */
    send(method: string, params: object = {}): void {
        const command = {method, params, id: method};
        if (this.connection === undefined) {
            throw new Error("First connect to the exchange before sending instructions..");
        }

        this.connection.send(JSON.stringify(command));
    }

    abstract subscribeCandles(pair: Pair, interval: ICandleInterval): void;

    abstract subscribeOrderbook(pair: Pair): void;

    /**
     * Listen for actions that are happening on the remote exchange
     */
    abstract subscribeReports(): void;

    /**
     * Adds order to local array
     * @param {IOrder} order the order to add
     */
    protected addOrder(order: IOrder): void {
        this.openOrders.push(order);
    }

    /**
     * Validates if adjusting an existing order on an exchange is allowed
     * @param order the order to check
     * @param price new price
     * @param qty new quantity
     */
    protected isAdjustingOrderAllowed(order: IOrder, price: number, qty: number): boolean {
        if (this.orderInProgress[order.id]) {
            return false; // Order still in progress
        }

        if (order.price === price && order.quantity === qty) {
            return false; // Old order === new order. No need to replace!
        }

        this.setOrderInProgress(order.id);
        return true;
    }

    /**
     * Load trading pair configuration
     */
    protected abstract loadCurrencies(): void;

    /**
     * Triggers when the exchange is connected to the websocket API
     * @param {connection} conn the connection
     */
    protected onConnect(conn: connection): void {
        this.connection = conn;
        this.connection.on("error", error => logger.error("Connection Error: " + error.toString()));
        this.connection.on("close", () => logger.info("Connection Closed"));
    }

    /**
     * Remove order from local array
     * @param {string} orderId of the order to remove
     */
    protected removeOrder(orderId: string): void {
        this.openOrders = this.openOrders.filter(o => o.id !== orderId);
    }

    /**
     * Sets the order in progress
     * @param {string} orderId of the order to set in progress
     * @param {boolean} state whether the order still has state
     */
    protected setOrderInProgress(orderId: string, state = true): void {
        if (state === false) {
            delete this.orderInProgress[orderId];
        } else {
            this.orderInProgress[orderId] = state;
        }
    }
}

import {EventEmitter} from "events";
import CandleCollection, {ICandle, ICandleInterval} from "../candleCollection";
import Orderbook from "../orderbook";
import {IOrder, OrderSide} from "../orderInterface";
import {Pair} from "../types/pair";
import {IOrderbookData, ITradeablePair} from "./baseExchange";

/**
 * The IExchange represents a marketplace to buy and sell
 * cryptocurrencies
 */
export interface IExchange extends EventEmitter {

    isAuthenticated: boolean;
    isCurrenciesLoaded: boolean;

    /**
     * Adjusts existing order on exchange
     * @param order the order to adjust
     * @param price the new price
     * @param qty the new quantity
     */
    adjustOrder(order: IOrder, price: number, qty: number): void;

    /**
     * Signals a buy to the exchange
     * @param {Pair} pair crypto pair (BTC USD/BTC ETH)
     * @param {number} price at which to buy/sell
     * @param {number} qty quantity to buy/sell
     */
    buy(pair: Pair, price: number, qty: number): void;

    /**
     * Cancel existing order on exchange
     * @param {IOrder} order to cancel
     */
    cancelOrder(order: IOrder): void;

    /**
     * Connects to the exchange with given connection properties
     * @param {string} connectionString web socket url to connect to
     */
    connect(connectionString?: string): void;

    /**
     * Places an order on the exchange
     * @param {Pair} pair crypto pair (BTC USD/BTC ETH)
     * @param {number} price at which to buy/sell
     * @param {number} qty quantity to buy/sell
     * @param {OrderSide} side buy or sell
     * @returns {string} order id
     */
    createOrder(pair: Pair, price: number, qty: number, side: OrderSide): void;

    /**
     * Removes the exchanges from the running application
     * i.e. removes the event listeners
     */
    destroy(): void;

    /**
     * Gets all orders that have not yet been filled
     * @returns {IOrder[]}
     */
    getOpenOrders(): IOrder[];

    /**
     * Gets all (of everyone) open buy and sell order for a given pair on the
     * exchange
     * @param pair crypto pair (BTC USD/BTC ETH)
     * @returns {Orderbook} open buys and sells for pair
     */
    getOrderbook(pair: Pair): Orderbook;

    /**
     * Fires when exchange is created
     */
    onCreate(): void;

    /**
     * Registers all tradeable currencies on exchange
     * @param {ITradeablePair[]} currencies
     */
    onCurrenciesLoaded(currencies: ITradeablePair[]): void;

    /**
     * Updates the local order when exchange sends updated information
     * @param {IOrder} order the order to update
     */
    onReport(order: IOrder): void;

    /**
     * Updates local candle collection with collection from exchange
     * @param {Pair} pair crypto pair (BTC USD/BTC ETH)
     * @param {ICandle[]} candles updated candles
     * @param {ICandleInterval} interval candle interval
     * @param {Extract<K extends keyof CandleCollection, "set" | "update">} method whether to update or overwrite
     */
    onUpdateCandles<K extends keyof CandleCollection>(pair: Pair, candles: ICandle[], interval: ICandleInterval, method: Extract<K, "set" | "update">): void;

    /**
     * Updates local order collection with collection from exchange
     * @param {IOrderbookData} orderBook the orders
     * @param {Extract<K extends keyof Orderbook, "setOrders" | "addIncrement">} method whether to update or overwrite
     */
    onUpdateOrderbook<K extends keyof Orderbook>(orderBook: IOrderbookData, method: Extract<K, "setOrders" | "addIncrement">): void;

    /**
     * Sends a sell signal to the exchange
     * @param {Pair} pair crypto pair (BTC USD/BTC ETH)
     * @param {number} price at which to sell
     * @param {number} qty to buy
     */
    sell(pair: Pair, price: number, qty: number): void;

    /**
     * Registers to a candle collection for a given pair/interval
     * e.g. BTC/ETH every 5 minutes
     * @param {Pair} pair crypto pair (BTC USD/BTC ETH)
     * @param {ICandleInterval} interval time interval
     */
    subscribeCandles(pair: Pair, interval: ICandleInterval): void;

    /**
     * Registers to order book for given pair
     * @param {Pair} pair crypto pair (BTC USD/BTC ETH)
     */
    subscribeOrderbook(pair: Pair): void;

    /**
     * Registers to order updates
     */
    subscribeReports(): void;
}

import crypto from "crypto";
import nanoid from "nanoid";
import {connection, IMessage} from "websocket";
import CandleCollection, {ICandle, ICandleInterval, IIntervalDict} from "../candleCollection";
import logger from "../logger";
import Orderbook from "../orderbook";
import {IOrder, OrderSide} from "../orderInterface";
import {Pair} from "../types/pair";
import BaseExchange, {IOrderbookData, IResponseMapper} from "./baseExchange";
import HitBTCMapper from "./hitBTCMapper";

export const CandleInterval: IIntervalDict = {
    ONE_MINUTE: {code: "M1", cron: "00 */1 * * * *"},
    THREE_MINUTES: {code: "M3", cron: "00 */3 * * * *"},
    FIVE_MINUTES: {code: "M5", cron: "00 */5 * * * *"},
    FIFTEEN_MINUTES: {code: "M15", cron: "00 */15 * * * *"},
    THIRTY_MINUTES: {code: "M30", cron: "00 */30 * * * *"},
    ONE_HOUR: {code: "H1", cron: "00 00 */1 * * *"},
    FOUR_HOURS: {code: "H4", cron: "00 00 2,6,10,14,18,22 * * *"},
    ONE_DAY: {code: "D1", cron: "00 00 00 */1 * *"},
    SEVEN_DAYS: {code: "D7", cron: "00 00 00 */7 * *"},
    ONE_MONTH: {code: "1M", cron: "00 00 00 00 */1 *"},
};

/**
 * The HitBTC class represent the HitBTC exchange
 * @see https://hitbtc.com/
 */
export default class HitBTC extends BaseExchange {

    readonly mapper: IResponseMapper = new HitBTCMapper(this);
    private static instance?: HitBTC;
    private sequence = 0;

    /**
     * Creates a new HitBTC exchange
     * @param {string} pubKey the public key for connecting
     * @param {string} secKey the secret key for connecting
     */
    constructor(private pubKey = "", private secKey = "") {
        super();
    }

    /**
     * Creates a singleton instance of HitBTC
     * @param {string} pKey public key
     * @param {string} sKey secret key
     * @returns {HitBTC} HitBTC instance
     */
    static getInstance(pKey = "", sKey = "") {
        if (!HitBTC.instance) {
            HitBTC.instance = new HitBTC(pKey, sKey);
        }
        return HitBTC.instance;
    }

    adjustOrder(order: IOrder, price: number, qty: number): void {
        if (this.isAdjustingOrderAllowed(order, price, qty)) {
            const newOrderId = this.generateOrderId(order.pair);

            this.send("cancelReplaceOrder", {
                clientOrderId: order.id,
                price,
                quantity: qty,
                requestClientId: newOrderId,
                strictValidate: true,
            });
        }
    }

    cancelOrder(order: IOrder): void {
        this.setOrderInProgress(order.id);
        this.send("cancelOrder", {clientOrderId: order.id});
    }

    connect() {
        super.connect("wss://api.hitbtc.com/api/2/ws");
    }

    createOrder(pair: Pair, price: number, qty: number, side: OrderSide): void {
        const orderId = this.generateOrderId(pair);
        this.setOrderInProgress(orderId);

        logger.info(`${side.toUpperCase()} ORDER! PRICE: ${price} SIZE: ${qty}`);
        this.send("newOrder", {
            clientOrderId: orderId,
            price,
            quantity: qty,
            side,
            symbol: pair,
            type: "limit",
        });
    }

    loadCurrencies(): void {
        this.send("getSymbols");
    }

    /**
     * Authenticates user on exchange using secure signed nonce
     * @param publicKey the public key
     * @param privateKey the private key
     */
    login(publicKey: string, privateKey: string): void {
        const nonce: string = nanoid(32);
        const signature: string = crypto.createHmac("sha256", privateKey).update(nonce).digest("hex");

        this.send("login", {
            algo: "HS256",
            nonce,
            pKey: publicKey,
            signature,
        });
    }

    onUpdateCandles<K extends keyof CandleCollection>(pair: Pair, data: ICandle[], interval: ICandleInterval, method: Extract<K, "set" | "update">): void {
        const candleCollection = this.getCandleCollection(pair, interval, candles => this.emit("app.updateCandles", candles));
        return candleCollection[method](data);
    }

    onUpdateOrderbook<K extends keyof Orderbook>(response: IOrderbookData, method: Extract<K, "setOrders" | "addIncrement">): void {
        if (response.sequence <= this.sequence) {
            logger.info(`Sequence dropped: ${response.sequence}, last one: ${this.sequence}`);
            return;
        }

        this.sequence = response.sequence;
        const orderbook: Orderbook = this.getOrderbook(response.pair);
        orderbook[method](response.ask, response.bid);

        this.emit("app.updateOrderbook", orderbook);
    }

    subscribeCandles = (pair: Pair, interval: ICandleInterval): void => this.send("subscribeCandles", {
        symbol: pair,
        period: interval.code,
    })

    subscribeOrderbook = (pair: Pair): void => this.send("subscribeOrderbook", {symbol: pair});

    subscribeReports = (): void => this.send("subscribeReports");

    protected onConnect(conn: connection): void {
        super.onConnect(conn);

        conn.on("message", (data: IMessage) => this.mapper.onReceive(data));

        this.loadCurrencies();

        if (this.pubKey !== "" && this.secKey !== "") {
            logger.info("Live credentials are used!");
            this.login(this.pubKey, this.secKey);
        }
    }
}

import {EventEmitter} from "events";
import moment from "moment";
import {Error} from "tslint/lib/error";
import {IMessage} from "websocket";
import {ICandle, ICandleInterval} from "../candleCollection";
import logger from "../logger";
import {IOrderbookEntry} from "../orderbook";
import {OrderSide, OrderStatus, OrderTimeInForce, OrderType, ReportType} from "../orderInterface";
import {Pair} from "../types/pair";
import BaseExchange, {IResponseMapper} from "./baseExchange";
import {CandleInterval} from "./hitBTC";

export interface IHitBTCOrderbookResponse {
    jsonrpc: string;
    method: string;
    params: {
        ask: IOrderbookEntry[],
        bid: IOrderbookEntry[],
        sequence: number,
        symbol: string,
    };
}

interface IHitBTCCandlesResponse {
    jsonrpc: string;
    method: string;
    params: {
        data: Array<{
            close: string;
            max: string;
            min: string;
            open: string;
            timestamp: string;
            volume: string;
            volumeQuote: string;
        }>;
        period: string;
        symbol: string;
    };
}

interface IHitBTCAuthenticateResponse {
    id: string;
    jsonrpc: string;
    result: boolean;
}

interface IHitBTCGetSymbolsResponse {
    id: string;
    jsonrpc: string;
    result: Array<{
        baseCurrency: string;
        feeCurrency: string;
        id: string;
        provideLiquidityRate: string;
        quantityIncrement: string;
        quoteCurrency: string;
        takeLiquidityRate: string;
        tickSize: string;
    }>;
}

interface IHitBTCReportResponse {
    jsonrpc: string;
    method: string;
    params: Array<{
        clientOrderId: string;
        createdAt: string;
        cumQuantity: string;
        id: string;
        originalRequestClientOrderId?: string;
        postOnly: boolean;
        price: string;
        quantity: string;
        reportType: string;
        side: string;
        status: string;
        symbol: string;
        timeInForce: string;
        type: string;
        updatedAt: string;
    }>;
}

/**
 * The HitBTCMapper maps incoming api events and wraps them with additional checks/logic
 */
export default class HitBTCMapper extends EventEmitter implements IResponseMapper {

    /**
     * Create a new HitBTCMapper
     * @param {BaseExchange} exchange the exchange to map events from
     */
    constructor(private exchange: BaseExchange) {
        super();

        // Listen for all events that onReceive will be throwing..
        this.on("api.snapshotCandles", data => this.onUpdateCandles(data, "set"));
        this.on("api.updateCandles", data => this.onUpdateCandles(data, "update"));
        this.on("api.snapshotOrderbook", data => this.onUpdateOrderbook(data, "setOrders"));
        this.on("api.updateOrderbook", data => this.onUpdateOrderbook(data, "addIncrement"));
        this.on("api.report", data => this.onReport(data));
        this.on("api.login", data => this.onLogin(data));
        this.on("api.getSymbols", data => this.onGetSymbols(data)); // @TODO fix!
    }

    /**
     * Removes HitBTCMapper listeners
     */
    destroy(): void {
        this.removeAllListeners();
    }

    /**
     * Emits received message as api event
     * @param {IMessage} msg
     */
    onReceive(msg: IMessage): void {
        if (msg.type !== "utf8") {
            throw new Error("Response is not UTF8!");
        }

        if (msg.utf8Data) {
            const d = JSON.parse(msg.utf8Data);
            this.emit(`api.${d.method || d.id}`, d);
        }
    }

    /**
     * Maps HitBTC data to candle collection
     * @param {IHitBTCCandlesResponse} data the date from hitBTC
     * @returns {ICandle[]} the candle collection
     */
    private mapCandles(data: IHitBTCCandlesResponse): ICandle[] {
        return data.params.data.map<ICandle>(candle => ({
            close: parseFloat(candle.close),
            high: parseFloat(candle.max),
            low: parseFloat(candle.min),
            open: parseFloat(candle.open),
            timestamp: moment(candle.timestamp).second(0).millisecond(0),
            volume: parseFloat(candle.volumeQuote),
        }));
    }

    /**
     * Wraps the returning symbols (allowed pairs)
     * @param {IHitBTCGetSymbolsResponse} response
     */
    private onGetSymbols(response: IHitBTCGetSymbolsResponse): void {
        const result = response.result.map(({id, tickSize, quantityIncrement, baseCurrency, quoteCurrency}) => ({
            id: [baseCurrency, quoteCurrency] as Pair,
            quantityIncrement: parseFloat(quantityIncrement),
            tickSize: parseFloat(tickSize),
        }));
        this.exchange.onCurrenciesLoaded(result);
    }

    /**
     * Wraps login callback
     * @param {IHitBTCAuthenticateResponse} data
     */
    private onLogin(data: IHitBTCAuthenticateResponse): void {
        this.exchange.isAuthenticated = data.result;
        this.exchange.isReady();
    }

    /**
     * Wraps incoming report (order updates)
     * @param {IHitBTCReportResponse} data report data
     */
    private onReport(data: IHitBTCReportResponse): void {
        data.params.forEach(report => {
            this.exchange.onReport({
                id: report.clientOrderId,
                originalId: (report.originalRequestClientOrderId) ? report.originalRequestClientOrderId : undefined,
                createdAt: moment(report.createdAt),
                price: parseFloat(report.price),
                quantity: parseFloat(report.quantity),
                reportType: report.reportType as ReportType,
                side: report.side as OrderSide,
                status: report.status as OrderStatus,
                pair: this.exchange.currencies[report.symbol].id,
                timeInForce: report.timeInForce as OrderTimeInForce,
                type: report.type as OrderType,
                updatedAt: moment(report.updatedAt),
            });
        });
    }

    /**
     * Wraps incoming updated candles
     * @param {IHitBTCCandlesResponse} data the candles
     * @param {"set" | "update"} method overwrite or update
     */
    private onUpdateCandles(data: IHitBTCCandlesResponse, method: "set" | "update") {
        let interval: ICandleInterval | undefined;
        Object.keys(CandleInterval).some(key => {
            if (CandleInterval[key].code === data.params.period) {
                interval = CandleInterval[key];
                return true;
            }
            return false;
        });

        if (interval !== undefined) {
            const pair = this.exchange.currencies[data.params.symbol].id;
            return this.exchange.onUpdateCandles(pair, this.mapCandles(data), interval, method);
        }

        logger.debug(`Interval: "${data.params.period}" is not recognized by the system. The exchange callback "onUpdateCandles" was not triggered.`);
    }

    /**
     * Wraps incoming orderbook
     * @param {{ask: IOrderbookEntry[]; bid: IOrderbookEntry[]; sequence: number; symbol: string}} ob the orderbook
     * @param {"addIncrement" | "setOrders"} method update or overwrite
     */
    private onUpdateOrderbook({params: ob}: IHitBTCOrderbookResponse, method: "addIncrement" | "setOrders"): void {
        this.exchange.onUpdateOrderbook({
            ask: ob.ask,
            bid: ob.bid,
            pair: this.exchange.currencies[ob.symbol].id,
            sequence: ob.sequence,
        }, method);
    }
}

import {ICandle} from "../candleCollection";
import {IOrder, OrderSide, OrderStatus, OrderTimeInForce, OrderType, ReportType} from "../orderInterface";
import {Pair} from "../types/pair";
import BaseExchange from "./baseExchange";

export interface IAssetMap {
    [key: string]: number;
}

/**
 * The LocalExchange resembles a local dummy marketplace for
 * strategy testing
 */
export default class LocalExchange extends BaseExchange {

    private static instance?: LocalExchange;
    private assets: IAssetMap = new Proxy<IAssetMap>({
        USD: 100000,
    }, {get: (target, p: PropertyKey): any => p in target ? target[p.toString()] : 0});
    private currentCandle?: ICandle;
    private filledOrders: IOrder[] = [];

    /**
     * Creates a new LocalExchange
     */
    constructor() {
        super();

        this.prependListener("app.updateCandles", (candles: ICandle[]) => this.processOpenOrders(candles[0]));
        this.on("app.report", (order: IOrder) => this.updateAssets(order));
    }

    /**
     * Returns singleton instance of local exchange
     * @returns {LocalExchange} the new local exchange
     */
    static getInstance() {
        if (!LocalExchange.instance) {
            LocalExchange.instance = new LocalExchange();
        }
        return LocalExchange.instance;
    }

    adjustOrder(order: IOrder, price: number, qty: number): void {
        if (!this.currentCandle) {
            throw new Error("Current candle undefined. Emit candles before adjusting an order.");
        }

        const newOrder: IOrder = {
            ...order,
            id: this.generateOrderId(order.pair),
            reportType: ReportType.REPLACED,
            updatedAt: this.currentCandle.timestamp,
            type: OrderType.LIMIT,
            originalId: order.id,
            quantity: qty,
            price,
        };

        const isAllowed = newOrder.side === OrderSide.BUY ? this.isBuyAllowed.bind(this) : this.isSellAllowed.bind(this);
        if (!isAllowed(newOrder, order)) return;

        this.setOrderInProgress(order.id);
        this.onReport(newOrder);
    }

    cancelOrder(order: IOrder): void {
        this.setOrderInProgress(order.id);

        const canceledOrder: IOrder = {
            ...order,
            reportType: ReportType.CANCELED,
        };

        this.onReport(canceledOrder);
    }

    createOrder(pair: Pair, price: number, qty: number, side: OrderSide): void {
        if (!this.currentCandle) {
            throw new Error("Current candle undefined. Emit candles before creating an order.");
        }

        const orderId = this.generateOrderId(pair);
        const candleTime = this.currentCandle.timestamp;

        const order: IOrder = {
            createdAt: candleTime,
            updatedAt: candleTime,
            status: OrderStatus.NEW,
            timeInForce: OrderTimeInForce.GOOD_TILL_CANCEL,
            id: orderId,
            type: OrderType.LIMIT,
            reportType: ReportType.NEW,
            side,
            pair,
            quantity: qty,
            price,
        };

        const isAllowed = order.side === OrderSide.BUY ? this.isBuyAllowed.bind(this) : this.isSellAllowed.bind(this);
        if (!isAllowed(order)) return;

        this.setOrderInProgress(orderId);
        this.onReport(order);
    }

    /**
     * Emits a collection of candles from a local file as if they were
     * sent from a real exchange
     * @param {ICandle[]} candles
     * @returns {Promise<void>} promise
     */
    async emitCandles(candles: ICandle[]) {
        const normCandles: ICandle[] = (candles[candles.length - 1].timestamp.isBefore(candles[0].timestamp))
            ? candles.reverse()
            : candles;

        normCandles.reduce<ICandle[]>((acc, val, idx) => {
            const processedCandles = [val, ...acc];
            this.currentCandle = val;
            this.emit("app.updateCandles", processedCandles);
            return processedCandles;
        }, []);
    }

    isReady(): boolean {
        this.emit("ready");
        return true;
    }

    loadCurrencies = (): void => undefined;

    // noinspection JSUnusedGlobalSymbols
    onUpdateCandles = (): void => undefined;

    // noinspection JSUnusedGlobalSymbols
    onUpdateOrderbook = (): void => undefined;

    /**
     * Checks if open order can be filled on each price update
     * @param {ICandle} candle the current candle
     */
    processOpenOrders(candle: ICandle): void {
        const openOrders: IOrder[] = [];
        this.openOrders.forEach(oo => {
            if (oo.createdAt.isAfter(candle.timestamp)) {
                return openOrders.push(oo); // Candle should be newer than order!
            }

            const order = {...oo, reportType: ReportType.TRADE, status: OrderStatus.FILLED};
            if (oo.side === OrderSide.BUY && candle.low < oo.price) {
                this.filledOrders.push(order);
                return this.onReport(order);
            }

            if (oo.side === OrderSide.SELL && candle.high > oo.price) {
                this.filledOrders.push(order);
                return this.onReport(order);
            }

            openOrders.push(oo);
        });
        this.openOrders = openOrders;
    }

    subscribeCandles = (): void => undefined;

    subscribeOrderbook = (): void => undefined;

    subscribeReports = (): void => undefined;

    /**
     * Calculates total price of order
     * @param {IOrder} order the order
     * @returns {number} total price
     */
    private getOrderPrice(order: IOrder) {
        return order.price * order.quantity;
    }

    /**
     * Checks if funds are sufficient for a buy
     * @param {IOrder} order the order to verify
     * @param {IOrder} oldOrder
     * @returns {boolean} is buy allowed
     */
    private isBuyAllowed(order: IOrder, oldOrder?: IOrder): boolean {
        const orderPrice: number = this.getOrderPrice(order);

        return this.assets[order.pair[1]] > orderPrice;
    }

    /**
     * Checks if current quantity of currency in possession
     * if sufficient for given sell order
     * @param {IOrder} order the order to verify
     * @param {IOrder} oldOrder
     * @returns {boolean} is sell allowed
     */
    private isSellAllowed(order: IOrder, oldOrder?: IOrder): boolean {
        const orderPrice: number = this.getOrderPrice(order);
        const oldOrderPrice = (oldOrder) ? this.getOrderPrice(oldOrder) : 0;

        return this.assets[order.pair[0]] > order.quantity;
    }

    // @TODO test and verify logic..
    /**
     * Updates the assets on the exchange for given new order
     * @param {IOrder} order new order
     * @param {IOrder} oldOrder old order
     */
    private updateAssets(order: IOrder, oldOrder?: IOrder) {
        const [target, source] = order.pair;

        // if (order.side === OrderSide.SELL) {
        //     target = order.pair[1];
        //     source = order.pair[0];
        // }

        if (ReportType.REPLACED === order.reportType && oldOrder) {
            // @TODO ..
        } else if (ReportType.NEW === order.reportType) {
            if (order.side === OrderSide.BUY) {
                this.assets[source] -= this.getOrderPrice(order);
            } else {
                this.assets[target] -= order.quantity;
            }
        } else if (ReportType.TRADE === order.reportType && OrderStatus.FILLED === order.status) {
            if (order.side === OrderSide.BUY) {
                this.assets[target] += order.quantity;
            } else {
                this.assets[source] += this.getOrderPrice(order);
            }
            console.log(this.assets);
        } else if ([ReportType.CANCELED, ReportType.EXPIRED, ReportType.SUSPENDED].indexOf(order.reportType) > -1) {
            this.assets[source] += this.getOrderPrice(order);
        }
    }
}

import {IExchange} from "./exchanges/exchangeInterface";
import {IOrder} from "./orderInterface";
import SockTrader from "./sockTrader";

/**
 * The LiveTrader enables you to run your strategy against
 * a live environment on an exchange
 */
export default class LiveTrader extends SockTrader {

    /**
     * Adds an exchange
     * @param {IExchange} exchange the exchange to add
     * @returns {this}
     */
    addExchange(exchange: IExchange): this {
        this.exchange = exchange;

        return this;
    }

    async start(): Promise<void> {
        await super.start();

        if (!this.exchange) {
            throw new Error("No exchange defined!");
        }

        if (!this.eventsBound) {
            this.subscribeToExchangeEvents(this.strategyConfigurations);

            this.strategyConfigurations.forEach(c => {
                const strategy = new c.strategy(c.pair, this.exchange);
                this.bindStrategyToExchange(strategy);
                this.bindExchangeToStrategy(strategy);
                this.bindExchangeToSocketServer();
            });

            this.eventsBound = true;
        }

        // @TODO cannot connect multiple times to the same exchange
        // -> start function might be called multiple times by the dashboard?
        this.exchange.connect();
    }

    private bindExchangeToSocketServer() {
        this.exchange.on("app.report", (order: IOrder) => this.sendToWebServer("REPORT", order));
        this.exchange.on("app.updateCandles", candles => this.sendToWebServer("CANDLE_UPDATE", candles));
    }
}

import moment from "moment";
import {createLogger, format, transports} from "winston";
const defaults = {silent: process.env.NODE_ENV === "test"};

const stringFormat = format.printf(info => {
    const {timestamp, level, message, ...args} = info;
    const ts = moment(timestamp).format("YYYY-MM-DD HH:mm:ss");
    return `${ts} [${level}]: ${message} ${Object.keys(args).length ? JSON.stringify(args, undefined, 2) : ""}`;
});

const consoleFormat = format.combine(
    format.colorize(),
    format.timestamp(),
    format.align(),
    stringFormat,
);

const fileFormat = format.combine(consoleFormat, format.uncolorize());

const getFileTransport = (level: any, file: any) => new transports.File({
    ...defaults,
    filename: `./src/logs/${file}.log`,
    format: fileFormat,
    level,
});

const getConsoleTransport = (level: any) => new transports.Console({...defaults, format: consoleFormat, level});

const errorLogger = createLogger({
    levels: {error: 3},
    transports: [getFileTransport("error", "error"), getConsoleTransport("error")],
});

const infoLogger = createLogger({
    levels: {info: 2},
    transports: [getFileTransport("info", "system"), getConsoleTransport("info")],
});

const debugLogger = createLogger({
    levels: {debug: 1},
    transports: [getFileTransport("debug", "debug")],
});

const inputLogger = createLogger({
    levels: {input: 0},
    transports: [getFileTransport("input", "input")],
});

export default {
    debug: (msg: string, ...meta: any[]) => debugLogger.debug(msg, meta),
    error: (msg: string, ...meta: any[]) => errorLogger.error(msg, meta),
    info: (msg: string, ...meta: any[]) => infoLogger.info(msg, meta),
    input: (msg: string, ...meta: any[]) => inputLogger.input(msg, meta),
};

import {Moment} from "moment";
import {Pair} from "./types/pair";

export enum OrderSide {
    BUY = "buy",
    SELL = "sell",
}

export enum OrderStatus {
    NEW = "new",
    SUSPENDED = "suspended",
    CANCELED = "canceled",
    EXPIRED = "expired",
    PARTIALLY_FILLED = "partiallyFilled",
    FILLED = "filled",
}

export enum ReportType {
    NEW = "new",
    SUSPENDED = "suspended",
    CANCELED = "canceled",
    EXPIRED = "expired",
    STATUS = "status",
    TRADE = "trade",
    REPLACED = "replaced",
}

export enum OrderType {
    LIMIT = "limit",
    MARKET = "market",
}

export enum OrderTimeInForce {
    GOOD_TILL_CANCEL = "GTC",
    IMMEDIATE_OR_CANCEL = "IOC",
    FILL_OR_KILL = "FOK",
    DAY = "DAY",
    GOOD_TILL_DATE = "GTD",
}

export interface IOrder {
    createdAt: Moment;
    id: string;
